        boolean result =
            apply(start + 1, end) && !charSets.get(subQuestionKey).contains(s.charAt(start));
        if (result) {
          Set charSet = new HashSet<>(charSets.get(subQuestionKey));
          charSet.add(s.charAt(start));
          charSets.put(key, charSet);
        }
        return result;
      }
    };
