import java.util.ArrayList;
import java.util.HashSet;
import java.util.List;
import java.util.Set;

public class Solution1046A {

  public int[][] merge(int[][] intervals) {
    List> components = new ArrayList<>();
    Set globalAccessed = new HashSet<>();
    for (int[] start : intervals) {
      List component = depthFirstSearch(intervals, start, new ArrayList(),
          globalAccessed);
      if (component.size() > 0) {
        components.add(component);
      }
    }
    int[][] result = new int[components.size()][];
    int index = 0;
    for (List component : components) {
      int start = Integer.MAX_VALUE;
      int end = Integer.MIN_VALUE;
      for (int[] interval : component) {
        start = Math.min(start, interval[0]);
        end = Math.max(end, interval[1]);
